import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import { fetchGenres, fetchSearchResults } from '../../util/API';

const Navbar = () => {
  const [genres, setGenres] = useState([]);
  const [showGenresDropdown, setShowGenresDropdown] = useState(false);
  const [showMoviesDropdown, setShowMoviesDropdown] = useState(false);

  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [showSearchResults, setShowSearchResults] = useState(false);

  const searchRef = useRef(null);

  const moviesOptions = [
    { name: "Top Rated", path: "top-rated" },
    { name: "Popular", path: "popular" },
    { name: "Latest", path: "latest" },
    { name: "Now Playing", path: "now-playing" },
    { name: "Upcoming", path: "upcoming" },
  ];

  useEffect(() => {
    const loadGenres = async () => {
      try {
        const genresData = await fetchGenres();
        setGenres(genresData.genres);
      } catch (error) {
        console.error("Error fetching genres:", error);
      }
    };

    loadGenres();
  }, []);

  const toggleDropdown = (setter) => {
    setter(prev => !prev);
  };

  const closeDropdown = (setter) => {
    setter(false);
  };

  const handleOutsideClick = (e) => {
    if (searchRef.current && !searchRef.current.contains(e.target)) {
      setShowSearchResults(false);
    }

    if (!e.target.closest(".dropdown")) {
      setShowGenresDropdown(false);
      setShowMoviesDropdown(false);
    }
  };

  useEffect(() => {
    document.addEventListener("mousedown", handleOutsideClick);
    return () => {
      document.removeEventListener("mousedown", handleOutsideClick);
    };
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery) return;

    try {
      const searchData = await fetchSearchResults(searchQuery);
      setSearchResults(searchData.results);
      setShowSearchResults(true);
    } catch (error) {
      console.error("Error fetching search results:", error);
    }
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchResultClick = () => {
    setShowSearchResults(false);
    setSearchQuery("");
  };

  return (
    <nav className="navbar">
      <ul className="navbar-menu">
        <li>
          <Link to="/" className="logo-text">
            MovieApp
          </Link>
        </li>
        <li>
          <Link to="/">Home</Link>
        </li>
        <li>
          <Link to="/actors">Actors</Link>
        </li>
        <li
          className="dropdown"
          onMouseEnter={() => toggleDropdown(setShowGenresDropdown)}
          onMouseLeave={() => closeDropdown(setShowGenresDropdown)}
        >
          <Link to="#">Genres</Link>
          {showGenresDropdown && (
            <ul className="dropdown-content">
              {genres.map((genre) => (
                <li key={genre.id}>
                  <Link
                    to={`/genres/${genre.id}`}
                    onClick={() => closeDropdown(setShowGenresDropdown)}
                  >
                    {genre.name}
                  </Link>
                </li>
              ))}
            </ul>
          )}
        </li>
        <li
          className="dropdown"
          onMouseEnter={() => toggleDropdown(setShowMoviesDropdown)}
          onMouseLeave={() => closeDropdown(setShowMoviesDropdown)}
        >
          <Link to="#">Movies</Link>
          {showMoviesDropdown && (
            <ul className="dropdown-content">
              {moviesOptions.map((option) => (
                <li key={option.path}>
                  <Link
                    to={`/movies/${option.path}`}
                    onClick={() => closeDropdown(setShowMoviesDropdown)}
                  >
                    {option.name}
                  </Link>
                </li>
              ))}
            </ul>
          )}
        </li>
        <li className="search-box" ref={searchRef}>
          <form onSubmit={handleSearch}>
            <input
              type="text"
              value={searchQuery}
              onChange={handleSearchChange}
              placeholder="Search movies or actors..."
            />
            <button type="submit">Search</button>
          </form>
          {showSearchResults && (
            <ul className="search-results">
              {searchResults.map((result) => (
                <li key={result.id}>
                  <Link
                    to={
                      result.media_type === "movie"
                        ? `/movies/${result.id}`
                        : `/actors/${result.id}`
                    }
                    onClick={handleSearchResultClick}
                  >
                    {result.title || result.name}
                  </Link>
                </li>
              ))}
            </ul>
          )}
        </li>
      </ul>
    </nav>
  );
};

export default Navbar;
